def start_command(update, context):
    update.message.reply_text(   
        "Hi! I am Mr. Chef, thank you for using my program, What'sMyMeal.\n"
        "Just tell me what you need and I will find you a recipe!\n"     
        "You can use the following commands to talk to me:\n"
        "The following commands are:\n"
        "/Help\n"
        "/Recipes\n"
        "/Vegan\n"
        "/Cocktail")


